/**
 * Anthropic Logo Icon
 * Based on the official Anthropic brand logo (stylized "A" shape)
 */
export function AnthropicIcon({ className, ...props }: IconProps) {
  return (
    <svg
      viewBox="0 0 24 24"
      fill="none"
      xmlns="http://www.w3.org/2000/svg"
      className={cn('h-5 w-5', className)}
      {...props}
    >
      <path
        d="M17.304 3H13.896L19.8 21H23.208L17.304 3Z"
        fill="currentColor"
      />
      <path
        d="M6.696 3L0.792 21H4.2L5.544 17.076H12.456L13.8 21H17.208L11.304 3H6.696ZM6.624 14.076L9 6.384L11.376 14.076H6.624Z"
        fill="currentColor"
      />
    </svg>
  );
}

