/**
 * Provider icon component that renders the appropriate icon based on provider type
 */
interface ProviderIconProps extends IconProps {
  provider: 'anthropic' | 'openai';
}

export function AIProviderIcon({ provider, className, ...props }: ProviderIconProps) {
  if (provider === 'anthropic') {
    return <AnthropicIcon className={className} {...props} />;
  }
  return <OpenAIIcon className={className} {...props} />;
}
